jQuery.noConflict();
;(function($) {
    'use strict';

    function Site(settings) {

        this.windowLoaded = false;

    }

    Site.prototype = {
        constructor: Site,

        start: function() {
            var me = this;

            $(window).load(function() {
                me.windowLoaded = true;
            });

            this.attach();
        },

        attach: function() {
            this.attachBootstrapPrototypeCompatibility();
            this.attachMedia();
        },

        attachBootstrapPrototypeCompatibility: function() {

            // Bootstrap and Prototype don't play nice, in the sense that
            // prototype is a really wacky horrible library. It'll
            // hard-code CSS to hide an element when a hide() event
            // is fired. See http://stackoverflow.com/q/19139063
            // To overcome this with dropdowns that are both
            // toggle style and hover style, we'll add a CSS
            // class which has "display: block !important"
            $('*').on('show.bs.dropdown show.bs.collapse', function(e) {
                $(e.target).addClass('bs-prototype-override');
            });

            $('*').on('hidden.bs.collapse', function(e) {
                $(e.target).removeClass('bs-prototype-override');
            });
        },

        attachMedia: function() {
            var $links = $('[data-toggle="media"]');
            if ( ! $links.length) { return; }

            // When somebody clicks on a link, slide the
            // carousel to the slide which matches the
            // image index and show the modal
            $links.on('click', function(e) {
                e.preventDefault();

                var $link = $(this),
                   $modal = $($link.attr('href')),
                $carousel = $modal.find('.carousel'),
                    index = parseInt($link.data('index'));

                $carousel.carousel(index);
                $modal.modal('show');

                return false;
            });
        }
    };

    jQuery(document).ready(function($) {
        var site = new Site();
        site.start();
    });

})(jQuery);

/* Kaptencheckbox
*  The Kaptencheckbox is a Linktag which is Stlyed with CSS before Pseudoelement. When it is clicked than the value of a hiddenfield inside of the Link should be 1
* */
jQuery(document).ready(function() {
    jQuery(".kaptencheckbox").click(function(e) {
        e.preventDefault();
        jQuery(this).toggleClass("checked");
        if(jQuery(this).find(".checkbox-value").val() == 0){
            jQuery(this).find(".checkbox-value").val(1);
        } else {
            jQuery(this).find(".checkbox-value").val(0);
        }
    });

    jQuery(".bestseller").each(function(e) {
        jQuery(this).find('.row').slick({
            dots: false,
            infinite: true,
            speed: 300,
            slidesToShow: 4,
            slidesToScroll: 4,
            responsive: [
                {
                    breakpoint: 992,
                    settings: {
                        slidesToShow: 2,
                        slidesToScroll: 2,
                        infinite: true,
                        dots: false
                    }
                },
                {
                    breakpoint: 768,
                    settings: {
                        slidesToShow: 1,
                        slidesToScroll: 1
                    }
                }
            ]
        });
    });
});

isAutoplaySupported = function(callback) {
    // Is the callback a function?
    if (typeof callback !== 'function') {
        console.log('isAutoplaySupported: Callback must be a function!');
        return false;
    }
    // Check if sessionStorage exist for autoplaySupported,
    // if so we don't need to check for support again
    if (!sessionStorage.autoplaySupported) {
        // Create video element to test autoplay
        var video = document.createElement('video');
        video.autoplay = true;
        video.muted = true;
        video.setAttribute('webkit-playsinline', 'webkit-playsinline');
        video.setAttribute('playsinline', 'playsinline');
        video.src = 'data:video/mp4;base64,';
        video.load();
        video.style.display = 'none';
        video.playing = false;
        video.play();
        // Check if video plays
        video.onplay = function() {
            this.playing = true;
        };
        // Video has loaded, check autoplay support
        video.oncanplay = function() {
            if (video.playing) {
                sessionStorage.autoplaySupported = 'true';
                callback(true);
            } else {
                sessionStorage.autoplaySupported = 'false';
                callback(false);
            }
        };
    } else {
        // We've already tested for support
        // use sessionStorage.autoplaySupported
        if (sessionStorage.autoplaySupported === 'true') {
            callback(true);
        } else {
            callback(false);
        }
    }
};
